mapper.insert:

insert into blog
values(null, #{userID}, #{blogName}, #{img});


select last_insert_id();

mapper.select:

select no, user_id as userID, blog_name as blogName, img
from blog
where no = #{no};


select no, user_id as userID, blog_name as blogName, img
from blog
where user_id = #{id};

mapper.update:

update blog
set blog_name = #{blogName}, img = #{img}
where user_id = #{id}
